import {useState, useEffect} from 'react';
import {
	TextField,
	Button,
	Table,
	TableBody,
	TableCell,
	tableCellClasses,
	TableContainer,
	TableHead,
	TableRow,
	Paper
} from '@mui/material';
import { styled } from '@mui/material/styles';
import {useSelector, useDispatch} from 'react-redux';
import DeleteIcon from "@material-ui/icons/Delete";
import EditIcon from "@material-ui/icons/Edit";
const moment = require('moment');


const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  '&:last-child td, &:last-child th': {
    border: 0,
  },
}));



function Todos(props){

	const lists = useSelector((state)=>state.lists.lists);
	console.log("List = ",lists);
	const [title, setTitle] = useState('');
	const [date, setDate] = useState('');
	const {list} = props;
	console.log("list = ", list);


	const onDelete = (e,id)=>{

	}

	const onEdit = (e,id) =>{

	}

	function createData(id,dueDate,title) {
		const editButton = <Button onClick={(e)=>onEdit(e,id)}><EditIcon/></Button>
		const deleteButton = <Button onClick={(e)=>onDelete(e,id)}><DeleteIcon style={{'color': 'red'}}/></Button>
	  	return { title, dueDate, id, editButton, deleteButton };
	}
	const rows = (!!list && !!list.todos.length) && list.todos.map(tableData => createData(tableData._id, tableData.dueDate, tableData.title));


	const onTodoAdd = () => {

	}


	console.log("Rows ============== ",rows);
	console.log(list);
	return(
		<>
			<div style={{'padding': '0em'}}>
				<TextField 
					style= {{ 'marginRight': '1em', 'boxShadow': '0 1px 6px rgba(32,33,36,.28)' }}
					id="outlined-basic" 
					label="Enter Name of todo"
					value = {title} 
					variant="outlined"
					size="small"
					onChange = {(event)=>setTitle(event.target.value)}
				/>
				<Button variant="contained" onClick={onTodoAdd}>Add Todo</Button>
			</div><br/>
			<div style={{'paddingTop': '1em'}}>
				<TableContainer sx={{ maxWidth: '75%','boxShadow': '0 1px 15px rgba(32,33,36,.28)'}} component={Paper}>
				<Table  aria-label="customized table">
				<TableHead>
				  <TableRow >
				    <StyledTableCell align="right">Title</StyledTableCell>
				    <StyledTableCell align="right">Due Date</StyledTableCell>				    
				    <StyledTableCell align="right">Edit</StyledTableCell>
				    <StyledTableCell align="right">Delete</StyledTableCell>
				  </TableRow>
				</TableHead>
				<TableBody>
				{
				  	(!!rows && rows.length) && rows.map((row) => (
				    <StyledTableRow key={row.id}>
						<StyledTableCell component="th" scope="row">
							{row.title}
						</StyledTableCell>
						<StyledTableCell align="right">{row.dueDate}</StyledTableCell>
						<StyledTableCell align="right">{row.editButton}</StyledTableCell>
						<StyledTableCell align="right">{row.deleteButton}</StyledTableCell>
					</StyledTableRow>
				  ))
				}
				</TableBody>
				</Table>
				</TableContainer>
			</div>
		</>	
	)
}

export default Todos;
