import {useState, useEffect} from 'react';
import {
	TextField,
	Button,
	Table,
	TableBody,
	TableCell,
	tableCellClassese,
	TableContainer,
	TableHead,
	TableRow,
	Paper
} from '@mui/material';
// import Todos from './e,Todos';
import { styled } from '@mui/material/styles';
import DeleteIcon from "@material-ui/icons/Delete";
import EditIcon from "@material-ui/icons/Edit";
import axios from 'axios';



const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  '&:last-child td, &:last-child th': {
    border: 0,
  },
}));




function TodoList(){

	const [input, setInput] = useState('');
	const [data, setData] = useState([]);
	const [isEdit, setIsEdit] = useState(false);
	const [{isTodo, setIsTodo}] = useState(false);

	useEffect(()=>{
		fetchData();
	},[]);

	async function fetchData(){
		try{
			let lists = await axios.get('http://localhost:3001/lists');
			setData(lists.data.data.listData);
		}catch(e){
				console.log("Error", e);
		}
	}

	const onAdd = async()=>{
		if(!isEdit){
			let newList = await axios.post('http://localhost:3001/lists',{
				name: input,
				todo: []
			})
			setData([...data,newList.data]);
			setInput('');
		}else{
			let newList = await axios.put(`http://localhost:3001/lists/${isEdit}`,{
				name: input,
				todo: []
			})
			setData(newList.data.data);
			setIsEdit(false);
			setInput('');
		}
	}

	const onEdit = (e,id)=>{
		e.stopPropagatio();ter(list=> list._id === id)[0];
		setInput(list.name);
		setIsEdit(id);
	}

	const onDelete = async (e,id)=>{

	try{
			let resp = await axios.delete(`http://localhost:3001/lists/delete/${id}`);
			setData(resp.data.data);

		}catch(e){
			console.log("Error: ", e);
		}
	}


	function createData(id,Name, Edit, Delete) {
		const editButton = <Button onClick={()=>onEdit(id)}><EditIcon/></Button>
		const deleteButton = <Button onClick={()=>onDelete(id)}><DeleteIcon style={{'color': 'red'}}/></Button>
	  	return { Name, editButton, deleteButton, };
	}

	const rows = (data.length) && data.map(tableData => createData(tableData._id, tableData.name));


	return(
		<>
			<center><h1>Todo List</h1></center>
			<div style={{'display': 'inline','padding': '1em'}} >
			  <TextField 
			  	style= {{ 'marginRight': '1em', 'boxShadow': '0 1px 6px rgba(32,33,36,.28)' }}
			  	id="outlined-basic" 
			  	label="Enter Name of list"
			  	value = {input} 
			  	variant="outlined"
			  	size="small"
			  	onChange = {(event)=>setInput(event.target.value)}
			  	/>
			  <Button style= {{ }} variant="contained" onClick={onAdd}>Add</Button>
			</div>
			<br/><br/>
			<div style={{'padding': '1em'}}>
				<TableContainer sx={{ maxWidth: '25em','boxShadow': '0 1px 15px rgba(32,33,36,.28)'}} component={Paper}>
				<Table  aria-label="customized table">
				<TableHead>
				  <TableRow >
				    <StyledTableCell align="right">Name</StyledTableCell>
				    <StyledTableCell align="right">Edit</StyledTableCell>
				    <StyledTableCell align="right">Delete</StyledTableCell>
				  </TableRow>
				</TableHead>
				<TableBody>
				  {(!!rows && rows.length) && rows.map((row) => (
				    <StyledTableRow style={{'cursor': 'pointer'}} key={row.name} onClick={()=>alert("Moazam")}>
						<StyledTableCell component="th" scope="row">
							{row.Name}
						</StyledTableCell>
						<StyledTableCell align="right">{row.editButton}</StyledTableCell>
						<StyledTableCell align="right">{row.deleteButton}</StyledTableCell>
					</StyledTableRow>
				  ))}
				</TableBody>
				</Table>
				</TableContainer>
			</div>
		</>
	);
}

export default TodoList;
