import * as types from '../types';


export function getLists(lists){
	return {
		type: types.GET_LISTS_REQUEST,
		payload: lists
	}
}

export function postLists(lists){
	return {
		type: types.POST_LIST_REQUEST,
		payload: lists
	}
}
