import {call,put,takeEvery,takeLatest} from 'redux-saga/effects';
import * as types from '../types';
import axios from 'axios';


function* postTodo(action){
	alert("in saga");
	try{
		let newTodos = yield axios.post('http://localhost:3001/list/todo/:id',action.payload);
		console.log("post todo resp = ",newTodos)
		yield put({type: types.POST_TODO_SUCCESS, payload: newTodos})
	}catch(e){
		yield put({type: types.POST_TODO_FAILURE, payload: e.message})	
	}
}


function* todoSaga(){
	yield takeEvery({type: types.POST_TODO_REQUEST, postTodo})
}

export default todoSaga;
