import * as type from '../types';


const initialState = {
	lists: [],
	isEdit: false,
	input: '',
}


export default function lists(state = initialState, action){
	console.log("Reducers => action = ",action);
	switch (action.type){
		case type.GET_LISTS_REQUEST: 
			return{
				...state,
				lists: action.payload
			}
		case type.GET_LISTS_SUCCESS:
			return{
				...state,
				lists: action.payload
			}
		case type.POST_LIST_SUCCESS:
			return{
				...state,
				lists: action.payload,
				input:''
			}
		default: 
			return state;
	}
}
