import {useState} from 'react';
import {
	TextField,
	Button,
	Table,
	TableBody,
	TableCell,
	tableCellClasses,
	TableContainer,
	TableHead,
	TableRow,
	Paper
} from '@mui/material';
import { styled } from '@mui/material/styles';
import DeleteIcon from "@material-ui/icons/Delete";
import EditIcon from "@material-ui/icons/Edit";



const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  '&:last-child td, &:last-child th': {
    border: 0,
  },
}));


function TodoList(){

	const [input, setInput] = useState('');
	const [data, setData] = useState([
		{
		  name: 'list 1'  
		},{
		  name: 'list 2'
		},{
		  name: 'list 3'
		},{
		  name: 'list 4'
		} 
	]);

	const onAdd = ()=>{
		console.log(input);
		setData([...data,{name:input}]);
	}

	const onEdit = ()=>{
		alert('Edit')
	}

	const onDelete = ()=>{
		alert('Delete')
	}

	const editButton = <Button style= {{ }} onClick={onEdit}><EditIcon/></Button>
	const deleteButton = <Button style= {{ }} onClick={onDelete}><DeleteIcon style={{'color': 'red'}}/></Button>

	function createData(Name, Edit, Delete) {
	  return { Name, Edit, Delete, };
	}

	const rows = data.map(tableData => createData(tableData.name,editButton,deleteButton));


	return(
		<>
			<center><h1>Todo List</h1></center>
			<div style={{'display': 'inline','padding': '1em'}} >
			  <TextField 
			  	style= {{ 'marginRight': '1em', 'boxShadow': '0 1px 6px rgba(32,33,36,.28)' }}
			  	id="outlined-basic" 
			  	label="Enter Name of list" 
			  	variant="outlined"
			  	size="small"
			  	onChange = {(event)=>setInput(event.target.value)}
			  	/>
			  <Button style= {{ }} variant="contained" onClick={onAdd}>app</Button>
			</div>
			<br/><br/>
			<div style={{'padding': '1em'}}>
				<TableContainer sx={{ maxWidth: '25em','boxShadow': '0 1px 15px rgba(32,33,36,.28)'}} component={Paper}>
				<Table  aria-label="customized table">
				<TableHead>
				  <TableRow>
				    <StyledTableCell align="right">Name</StyledTableCell>
				    <StyledTableCell align="right">Edit</StyledTableCell>
				    <StyledTableCell align="right">Delete</StyledTableCell>
				  </TableRow>
				</TableHead>
				<TableBody>
				  {rows.map((row) => (
				    <StyledTableRow key={row.name}>
				      <StyledTableCell component="th" scope="row">
				        {row.Name}
				      </StyledTableCell>
				      <StyledTableCell align="right">{row.Edit}</StyledTableCell>
				      <StyledTableCell align="right">{row.Delete}</StyledTableCell>
				    </StyledTableRow>
				  ))}
				</TableBody>
				</Table>
				</TableContainer>
			</div>
		</>
	);
}

export default TodoList;
