import {useState, useEffect} from 'react';
import {
	TextField,
	Button,
	Table,
	TableBody,
	TableCell,
	tableCellClasses,
	TableContainer,
	TableHead,
	TableRow,
	Paper
} from '@mui/material';
import { styled } from '@mui/material/styles';
import {useSelector, useDispatch} from 'react-redux';

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  '&:last-child td, &:last-child th': {
    border: 0,
  },
}));



function Todos(props){

	const list = useSelector((state)=>state.lists.lists);
	console.log("List = ",lists);

	const {listId} = props;
	console.log("listID = ", listId);

	function createData(id,Name) {
		// const editButton = <Button onClick={(e)=>onEdit(e,id)}><EditIcon/></Button>
		// const deleteButton = <Button onClick={(e)=>onDelete(e,id)}><DeleteIcon style={{'color': 'red'}}/></Button>
	  	return { Name, id };
	}


	const rows = (!!props.list && !!props.list.todos.length) && props.list.todos.map(tableData => createData(tableData._id, tableData.title));
	console.log("Rows ============== ",rows);
	console.log(props.listId);
	return(
		<>
			<div style={{'padding': '0em'}}>
				<TextField 
					style= {{ 'marginRight': '1em', 'boxShadow': '0 1px 6px rgba(32,33,36,.28)' }}
					id="outlined-basic" 
					label="Enter Name of todo"
					// value = {input} 
					variant="outlined"
					size="small"
					// onChange = {(event)=>setInput(event.target.value)}
				/>
				<Button variant="contained" onClick={()=>alert('hi')}>Add Todo</Button>
			</div><br/>
			<div style={{'paddingTop': '1em'}}>
				<TableContainer sx={{ maxWidth: '25em','boxShadow': '0 1px 15px rgba(32,33,36,.28)'}} component={Paper}>
				<Table  aria-label="customized table">
				<TableHead>
				  <TableRow >
				    <StyledTableCell align="right">Title</StyledTableCell>
				    <StyledTableCell align="right">Due Date</StyledTableCell>				    
				    <StyledTableCell align="right">Edit</StyledTableCell>
				    <StyledTableCell align="right">Delete</StyledTableCell>
				  </TableRow>
				</TableHead>
				<TableBody>
				{
				  	(!!rows && rows.length) && rows.map((row) => (
				    <StyledTableRow key={row.id}>
						<StyledTableCell component="th" scope="row">
							{row.Name}
						</StyledTableCell>
						<StyledTableCell align="right">{row.editButton}</StyledTableCell>
						<StyledTableCell align="right">{row.deleteButton}</StyledTableCell>
					</StyledTableRow>
				  ))
				}
				</TableBody>
				</Table>
				</TableContainer>
			</div>
		</>	
	)
}

export default Todos;
