import * as type from '../types';


const initialState = {
	lists: [],
}


export default function lists(state = initialState, action){
	console.log("action = ", !!action.payload && !!action.payload.data  ? action.payload.data : action);
	switch (action.type){
		case type.GET_LISTS_SUCCESS:
			return{
				...state,
				lists: action.payload
			}
		case type.POST_LIST_SUCCESS:
			return{
				...state,
				lists: action.payload,
			}
		case type.PUT_LIST_SUCCESS:
			return{
				...state,
				lists: action.payload.data,
			}
		case type.DELETE_LIST_SUCCESS:
		return{
			...state,
			lists: action.payload
		}
		case type.POST_TODO_SUCCESS:
			let newList = state.lists.filter(list=> list._id !== action.payload.data._id);
			return{
				...state,
				lists: [...newList, action.payload.data ]
			}
		case type.PUT_TODO_SUCCESS:
			newList = null;
			newList = state.lists.filter(list=> list._id !== action.payload._id);
			console.log("NewList === ",newList);
			return{
				...state,
				lists: [...newList, action.payload.data]
			}
		default: 
			return state;
	}
}
