import {useState} from 'react';
import {
	TextField,
	Button,
	// styled,
	Table,
	TableBody,
	TableCell,
	tableCellClasses,
	TableContainer,
	TableHead,
	TableRow,
	Paper
} from '@mui/material';
import { styled } from '@mui/material/styles';
import {DeleteIcon, EditIcon} from "@material-ui/icons";




const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  '&:last-child td, &:last-child th': {
    border: 0,
  },
}));


function TodoList(){


	const [data, setData] = useState([
		{
		  name: 'list 1'  
		},{
		  name: 'list 2'
		},{
		  name: 'list 3'
		},{
		  name: 'list 4'
		} 
	]);

	const editButton = <Button style= {{ }} variant="contained" onClick={()=>alert(1)}><EditIcon /></Button>
	const deleteButton = <Button style= {{ }} variant="contained" onClick={()=>alert(1)}>Delete</Button>

	function createData(Name, Edit, Delete) {
	  return { Name, Edit, Delete, };
	}

	const rows = data.map(tableData => createData(tableData.name,editButton,deleteButton));



	return(
		<>
			<center><h1>Todo List</h1></center>
			<div style={{'display': 'inline','padding': '1em'}} >
			  <TextField 
			  	style= {{ 'marginRight': '1em' }}
			  	id="outlined-basic" 
			  	label="List" 
			  	variant="outlined"
			  	size="small"
			  	/>
			  <Button style= {{ }} variant="contained" onClick={()=>alert(1)}>Add</Button>
			</div>
			<br/><br/>
			<TableContainer sx={{ maxWidth: '25em', padding: '1em' }} component={Paper}>
			<Table  aria-label="customized table">
			<TableHead>
			  <TableRow>
			    <StyledTableCell align="right">Name</StyledTableCell>
			    <StyledTableCell align="right">Edit</StyledTableCell>
			    <StyledTableCell align="right">Delete</StyledTableCell>
			  </TableRow>
			</TableHead>
			<TableBody>
			  {rows.map((row) => (
			    <StyledTableRow key={row.name}>
			      <StyledTableCell component="th" scope="row">
			        {row.Name}
			      </StyledTableCell>
			      <StyledTableCell align="right">{row.Edit}</StyledTableCell>
			      <StyledTableCell align="right">{row.Delete}</StyledTableCell>
			    </StyledTableRow>
			  ))}
			</TableBody>
			</Table>
			</TableContainer>
		</>
	);
}

export default TodoList;
